using Microsoft.UI.Xaml;namespace ContextWinUI;public partial class App : Application{	public static MainWindow? MainWindow { get; private set; }	public App()	{		InitializeComponent();		System.Text.Encoding.RegisterProvider(System.Text.CodePagesEncodingProvider.Instance);	}	protected override void OnLaunched(Microsoft.UI.Xaml.LaunchActivatedEventArgs args)	{		MainWindow = new MainWindow();		MainWindow.Activate();	}}